            <Route exact path="/"/>
            <Route path="/pentest/"/>
            <Route path="/active-directory/"/>
            <Route path="/ntlm/"/>
            <Route path="/smb/"/>
            <Route path="/kerberos/"/>
            <Route path="/sneakymercury/"/>
            <Route path="/redteam/"/>
            <Route path="/hacker-manifesto/"/>
            <Route path="/portfolio/"/>
          </Switch>
        </Menu>
    </Router>
  );
  }
}

export default Sidebar;
